window.addEventListener('DOMContentLoaded', function(e) {
  var iconName = "search-icon";
  var inputName = "search-input";
  var toggleName = "search-toggle";
  var moreName = "more-open";

  var iconElm = document.getElementById(iconName);
  var inputElm = document.getElementById(inputName);
  var toggleElm = document.getElementById(toggleName);
  var moreElm = document.getElementById(moreName);

  function blogsearchInnerToggle(e) {

    document.body.classList.toggle("search-active");
    iconElm.classList.toggle("fa-times");
    iconElm.classList.toggle("fa-search");

    if (document.querySelector('body.search-active')) {
      inputElm.focus();
    } else {
      inputElm.blur();
      toggleElm.blur();
    }        
  }
  toggleElm.addEventListener('click', blogsearchInnerToggle);

  function blogsearchOuterToggle(e) {
    function isChildOf(child, parent) {
      if (child.parentNode === parent) {
        return true;
      } else if (child.parentNode === null) {
        return false;
      } else {
        return isChildOf(child.parentNode, parent);
      }
    }

    var contentArea = document.getElementById('BlogSearch1');
    var target = e.target;

    if (target !== contentArea && !isChildOf(target, contentArea)) {
      if (document.querySelector('body.search-active')) {  
        document.body.classList.toggle("search-active");
        iconElm.classList.toggle("fa-times");
        iconElm.classList.toggle("fa-search");

        inputElm.blur();
      }
    }
  }
  document.addEventListener('click', blogsearchOuterToggle);
  if (moreElm) {
    moreElm.addEventListener('click', blogsearchOuterToggle);
  }
});

