  var pageElm = null;
  var labelElm = null;

  if (linkElm.length >= 1) {
    if (pathUrl == "/p/") {
      pageElm = linkElm[0].parentElement;
      pageElm.classList.add("selected-item");
      pageElm.classList.add("selected");
    } else {
      labelElm = linkElm[0].parentElement;
      labelElm.classList.add("selected-item");
      labelElm.classList.add("selected");
