body.script:
window.addEventListener('DOMContentLoaded', function(e) {
  var debounceTimer;

  function spacerRefresh(e) {
    var target = e.target;
    var currentTarget = e.currentTarget;

    var labelName = "Label1";

    var labelElm = document.getElementById(labelName);
    var navElm = labelElm.querySelector('nav');
    var labelContainer = labelElm.querySelectorAll(".label-container")[0];
    var labelItems = labelElm.querySelectorAll(".dd-inner a");
    var lastItem = labelItems[labelItems.length - 1];
    var burgerElm = document.getElementById('menu-toggle');

    var panelElm = document.getElementById('more-panel');

    var headerElm = document.getElementsByTagName('header')[0];
    var cloneElm = headerElm.cloneNode(true);
    var headerOuterElm = document.getElementsByTagName('header')[0].firstElementChild;
    var spacerElm = document.getElementById('float-panel-spacer');

    var pseudoElm = window.getComputedStyle(burgerElm, ':before').getPropertyValue('content');


    function spacerHeight(elm) {
      spacerElm.style.height = elm.clientHeight + "px";
    }


    if (currentTarget === window && e.type === "load") {
      spacerHeight(headerElm);
    }
    if (currentTarget === window && e.type === "resize") {
      window.clearTimeout(debounceTimer);
      debounceTimer = window.setTimeout(function() {
        if (headerElm.classList.contains("fixed")) {
          cloneElm.style.visibility = "hidden";
          cloneElm.classList.remove("fixed");
          if (headerElm.querySelector("#more-open").classList.contains("over")) {
            cloneElm.querySelector("#more-open").style.zIndex = 1;
            cloneElm.querySelector("#more-open").classList.remove("over");
          }
          headerElm.insertAdjacentElement("afterEnd", cloneElm);
          spacerHeight(cloneElm);
          cloneElm.parentElement.removeChild(cloneElm);
        } else {
          spacerHeight(headerElm);
        }
      }, 250);
    }

    if (pseudoElm === "none") {
      if (target === lastItem && !headerElm.classList.contains("fixed")) {
        spacerHeight(headerElm);
      }
      if (target === panelElm && labelElm.classList.contains("is-active")) {
        labelElm.classList.remove("is-active");
      }

    } else {
      if (target === burgerElm && e.type === "click" && !labelElm.classList.contains("is-active")) {
        labelElm.classList.add("is-active");
      }
      if (target === labelContainer && e.type === "transitionend" && !burgerElm.classList.contains("menu-icon-active") && labelElm.classList.contains("is-active")) {
        labelElm.classList.remove("is-active");
      }
      if (e.propertyName === "transform" || (e.propertyName === "min-height" || (e.propertyName === "height" && this === headerOuterElm && currentTarget === headerOuterElm)) && burgerElm.classList.contains("menu-icon-active")) {
        window.setTimeout(function() {spacerHeight(navElm);}, 250);
      }
      if ((e.propertyName === "transform" || e.propertyName === "height") && !burgerElm.classList.contains("menu-icon-active")) {
        spacerHeight(navElm);
      }
    }
  }

  var labelName = "Label1";

  var burgerElm = document.getElementById('menu-toggle');
  var headerOuterElm = document.getElementsByTagName('header')[0].firstElementChild;

  if (burgerElm) {
    burgerElm.addEventListener('click', spacerRefresh);
    headerOuterElm.addEventListener('transitionend', spacerRefresh);
    window.addEventListener('resize', spacerRefresh);
    window.addEventListener('load', spacerRefresh);
  }
});

window.addEventListener('DOMContentLoaded', function(e) {
  var debounceTimer;

  function moreToggle(e) {
    var target = e.target;
    var burgerElm = document.getElementById('menu-toggle');
    var bodyElm = document.body;
    var headerElm = document.getElementsByTagName("header")[0];
    var headerOuterElm = document.getElementsByTagName('header')[0].firstElementChild;

    var labelName = "Label1";
    var labelElm = document.getElementById(labelName);
    var navElm = labelElm.querySelector('nav');
    var labelItems = labelElm.querySelectorAll(".dd-inner a");
    var lastItem = labelItems[labelItems.length - 1];

    var moreElm = document.getElementById('more-open');

    if (e.propertyName == "z-index") {
      if (target.style.zIndex == 2) {  
        document.body.classList.add("more-active");
      } else {
        document.body.classList.remove("more-active");
      }

      if (!headerElm.classList.contains("fixed") && !burgerElm.classList.contains("menu-icon-active")) {
        if (target.style.zIndex == 2) {
          document.body.classList.add("menu-active");
          bodyScrollLock.disableBodyScroll(headerOuterElm);
        } else {
          document.body.classList.remove("menu-active");
          bodyScrollLock.enableBodyScroll(headerOuterElm);
        }
      }
    }

    if (moreElm.style.zIndex == 2 && target === lastItem && !burgerElm.classList.contains("menu-icon-active") && !bodyElm.classList.contains("menu-active")) {
      if (!headerElm.classList.contains("fixed")) {
        document.body.classList.add("menu-active");
        bodyScrollLock.disableBodyScroll(headerOuterElm);
      }
    }
  }

  function menuToggleMobile(e) {
    var target = e.target;
    var headerOuterElm = document.getElementsByTagName('header')[0].firstElementChild;

    if (this.classList.contains("menu-icon-active")) {
      document.body.classList.remove("menu-active");
      bodyScrollLock.enableBodyScroll(headerOuterElm);
    } else {
      document.body.classList.add("menu-active");
      bodyScrollLock.disableBodyScroll(headerOuterElm);
    }  
  }

  function moreRemoveMobile(e) {
    var target = e.target;
    var moreElm = document.getElementById('more-open');

    if (this.classList.contains("menu-icon-active") && moreElm.style.zIndex == 2) {
      document.body.classList.remove("more-active");
    }      
  }

  function menuRemoveMobile(e) {
    var burgerElm = document.getElementById('menu-toggle');
    var pseudoElm = window.getComputedStyle(burgerElm, ':before').getPropertyValue('content');
    var moreElm = document.getElementById('more-open');
    var searchElm = document.getElementById('search-toggle');

    if (e.type == "resize" && e.target == window) {
      window.clearTimeout(debounceTimer);
      debounceTimer = window.setTimeout(function() {
        if (pseudoElm === "none" && burgerElm.classList.contains("menu-icon-active")) {
          burgerElm.click();
          document.body.classList.remove("more-active");
        }
      }, 250);
    }
    if (e.type == "click" && e.currentTarget == searchElm && burgerElm.classList.contains("menu-icon-active")) {
      burgerElm.click();
    }
  }

  var labelName = "Label1";
  var labelElm = document.getElementById(labelName);
  var moreElm = document.getElementById('more-open');
  var burgerElm = document.getElementById('menu-toggle');
  var searchElm = document.getElementById('search-toggle');

  if (moreElm && burgerElm) {
    moreElm.style.zIndex = 1;

    moreElm.addEventListener('transitionend', moreToggle);
    burgerElm.addEventListener('click', menuToggleMobile);
    burgerElm.addEventListener('click', moreRemoveMobile);
    window.addEventListener("resize", menuRemoveMobile);
    searchElm.addEventListener("click", menuRemoveMobile);
  }
});

