window.addEventListener('load', function(e) {
  var pagerOlderName = "#Blog1_blog-pager-older-link";
  var pagerNewerName = "#Blog1_blog-pager-newer-link";
  var linkElm;
  var scriptElm;
  var headElm;

  var relOlderElm = document.querySelector(pagerOlderName + '[data-rel]');
  var relNewerElm = document.querySelector(pagerNewerName + '[data-rel]');

