window.addEventListener('DOMContentLoaded', function(e) {
  var sidebarLeftSel = "#sidebar-left-1";
  var sidebarRightSel = "#sidebar-right-1";

  var sidebarLeftElm = null;
  var sidebarRightElm = null;

  sidebarLeftElm = document.querySelector(sidebarLeftSel + ":not([class~='no-items'])");
  sidebarRightElm = document.querySelector(sidebarRightSel + ":not([class~='no-items'])");

  if (sidebarLeftElm && !sidebarRightElm) {
    document.body.classList.add("sidebar-left");
  }
  else if (sidebarRightElm && !sidebarLeftElm) {
    document.body.classList.add("sidebar-right");
  }
  else {
    document.body.classList.add("sidebar-none");
  }
});

